function bisect_left(a, x, left, right)
    lo = left or 1
    hi = right or nil

    while lo < hi do
        mid = math.floor((lo+hi) / 2)
        if a[mid] < x then
            lo = mid+1
        else
            hi = mid
        end
    end
    return lo
end

